import React from 'react';

import {
    BrowserRouter as Router,
    Routes,
    Route,
    Link
} from 'react-router-dom'
import {
    Collapse,
    Navbar,
    NavbarToggler,
    NavbarBrand,
    Nav,
    NavItem,
    NavLink,
    Input,
    Button
} from 'reactstrap';

import Today from './Today.js'
import './Main.css';

export default class Main extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            unit: 'metric',
            navbarToggle: false,
            searchText: ''
        };
        this.searchEl = null;

        this.handleNavbarToggle = this.handleNavbarToggle.bind(this);
        this.handleSearchKeyPress = this.handleSearchKeyPress.bind(this);
        this.handleClearSearch = this.handleClearSearch.bind(this);
        this.handleUnitChange = this.handleUnitChange.bind(this);
    }
    render() {
        return (
            <Router>
                <div className='main bg-faded'>
                    <div className='container'>
                        <Navbar color="faded" light>
                            <NavbarBrand className='text-info' href="/">WeatherMood</NavbarBrand>
                            <NavbarToggler onClick={this.handleNavbarToggle} />
                            <Collapse isOpen={this.state.navbarToggle}>
                                Now i can collapse it finally! {this.state.navbarToggle}
                            </Collapse>
                        </Navbar>
                    </div>
                    {/* <Routes>
                        <Route path="/" element={
                            <Nav navbar>
                                <NavItem>
                                    <NavLink tag={Link} to="/">Home</NavLink>
                                </NavItem>
                                <NavItem>
                                    <NavLink tag={Link} to="/forecast">Forecast</NavLink>
                                </NavItem>
                                <NavItem>
                                    <NavLink tag={Link} to="/blog">Blog</NavLink>
                                </NavItem>
                            </Nav>} />
                        <Route index element={<Today />} />
                        <Route path="/forecast" element={<p>come to the forecast!</p>} />
                        <Route path="/blog" element={<p>come to the blog!</p>} />
                    </Routes> */}
                    <div className='footer'>
                        Chencharrr
                    </div>
                </div>
            </Router>
        );
    }

    handleNavbarToggle() {
        this.setState((prevState, props) => ({
            navbarToggle: !prevState.navbarToggle
        }));
    }

    handleSearchKeyPress(e) {
        var keyCode = e.keyCode || e.which;
        if (keyCode === 13) {
            this.setState({
                searchText: e.target.value
            });
        }
    }

    handleClearSearch() {
        this.setState({
            searchText: ''
        });
        this.searchEl.value = '';
    }

    handleUnitChange(unit) {
        this.setState({
            unit: unit
        });
    }
}
